import { useState } from "react";

const FULL_SIZE = 40; // Fixed full board size
const INITIAL_ACTIVE_SIZE = 9; // Initial playable area
const EXPANSION_MARGIN = 1; // How close to the edge before expansion
const EXPANSION_STEP = 2; // Expand by 2 in each direction

const useGameLogic = () => {
    const [board, setBoard] = useState(Array(FULL_SIZE * FULL_SIZE).fill(null));
    const [currentPlayer, setCurrentPlayer] = useState("X");
    const [winner, setWinner] = useState(null);
    const [offset, setOffset] = useState((FULL_SIZE - INITIAL_ACTIVE_SIZE) / 2);
    
    // Start centered within FULL_SIZE
    const [activeTopLeft, setActiveTopLeft] = useState({
        row: Math.floor((FULL_SIZE - INITIAL_ACTIVE_SIZE) / 2),
        col: Math.floor((FULL_SIZE - INITIAL_ACTIVE_SIZE) / 2),
    });
    
    const [activeSize, setActiveSize] = useState(INITIAL_ACTIVE_SIZE);

    const handleClick = (index) => {
        if (winner) return; // Stop clicks after win
    
        // Calculate the row & col in FULL_SIZE board
        const row = Math.floor(index / activeSize);
        const col = index % activeSize;
        
        // Convert to FULL_SIZE board index
        const fullIndex = (row + offset) * FULL_SIZE + (col + offset);
        
        if (board[fullIndex] !== null) return; // Prevent overwriting
    
        const newBoard = [...board];
        newBoard[fullIndex] = currentPlayer;
        setBoard(newBoard);
    
        if (checkWinner(newBoard, activeSize, currentPlayer)) {
            setWinner(currentPlayer);
        } else {
            setCurrentPlayer(currentPlayer === "X" ? "O" : "X");
        }
    };    

    const expandActiveBoard = () => {
        if (activeSize + 4 <= FULL_SIZE) {
            setActiveSize(activeSize + 4);
            setOffset((FULL_SIZE - (activeSize + 4)) / 2);
        }
    };

    // Checks for 5-in-a-row win condition
    const checkWinner = (board, currentPlayer) => {
        const grid = [];
        
        // Convert 1D board into a 2D grid
        for (let row = 0; row < FULL_SIZE; row++) {
            grid.push(board.slice(row * FULL_SIZE, (row + 1) * FULL_SIZE));
        }

        const checkLine = (cells) => {
            for (let i = 0; i <= cells.length - 5; i++) {
                if (cells.slice(i, i + 5).every(cell => cell === currentPlayer)) {
                    return true;
                }
            }
            return false;
        };

        // Check rows
        for (let row = activeTopLeft.row; row < activeTopLeft.row + activeSize; row++) {
            if (checkLine(grid[row].slice(activeTopLeft.col, activeTopLeft.col + activeSize))) return true;
        }

        // Check columns
        for (let col = activeTopLeft.col; col < activeTopLeft.col + activeSize; col++) {
            let column = grid.map(row => row[col]).slice(activeTopLeft.row, activeTopLeft.row + activeSize);
            if (checkLine(column)) return true;
        }

        // Check diagonals
        for (let startRow = activeTopLeft.row; startRow <= activeTopLeft.row + activeSize - 5; startRow++) {
            for (let startCol = activeTopLeft.col; startCol <= activeTopLeft.col + activeSize - 5; startCol++) {
                let mainDiagonal = [];
                let antiDiagonal = [];

                for (let i = 0; i < 5; i++) {
                    mainDiagonal.push(grid[startRow + i]?.[startCol + i]);
                    antiDiagonal.push(grid[startRow + i]?.[startCol + 4 - i]);
                }

                if (checkLine(mainDiagonal) || checkLine(antiDiagonal)) return true;
            }
        }
        return false;
    };

    const resetGame = () => {
        setBoard(Array(FULL_SIZE * FULL_SIZE).fill(null));
        setCurrentPlayer("X");
        setWinner(null);
        setActiveSize(INITIAL_ACTIVE_SIZE);
        setActiveTopLeft({
            row: Math.floor((FULL_SIZE - INITIAL_ACTIVE_SIZE) / 2),
            col: Math.floor((FULL_SIZE - INITIAL_ACTIVE_SIZE) / 2),
        });
    };

    return { board, currentPlayer, winner, activeSize, activeTopLeft, handleClick, resetGame };
};

export default useGameLogic;
