import React from "react";
import { Card, CardContent, Typography, CardMedia } from "@mui/material";

const CustomCard = ({ title, description, image }) => {
  return (
    <Card sx={{ maxWidth: 345, m: 2, backgroundColor: "transparent", boxShadow: "none" }}> 
      {/* Image (transparent background support) */}
      {image && (
        <CardMedia 
          component="img" 
          height="140" 
          image={image} 
          alt={title} 
          sx={{ backgroundColor: "transparent", objectFit: "contain" }} // Ensure transparency
        />
      )}

      <CardContent>
        <Typography variant="h5" component="div">
          {title}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {description}
        </Typography>
      </CardContent>
    </Card>
  );
};

export default CustomCard;
