import React, { useState } from "react";
import "../style.css";
import useGameLogic from "../hooks/GameLogicHook";
const Board = ({ size }) => {
    //Set game logic and handle gamestate
    const { board, currentPlayer, winner, handleClick, resetGame } = useGameLogic(size);

    return (
        <div>
            <h2>{winner ? `Winner: ${winner}!` : `Current Player: ${currentPlayer}`}</h2>
            <div 
            className="board"
            style={{ 
                display: "grid",
                gridTemplateColumns: `repeat(${size}, 1fr)`,
                gridTemplateRows: `repeat(${size}, 1fr)`,
            }}
            >
            {board.map((cell, index) => (
                <div
                    key={index} 
                    className={`cell ${cell ? "active" : ""}`}
                    onClick={() => handleClick(index)}
                >
                    {cell}
                </div>
            ))}
            </div>
            {winner && <button onClick={resetGame}>Restart</button>}
        </div>
    );
};

export default Board;
