import React from "react";
import "../style.css";
import useGameLogic from "../hooks/GameLogicHook";

const Board = () => {
    // Set game logic and handle game state
    const { board, currentPlayer, winner, activeSize, handleClick, resetGame } = useGameLogic();

    return (
        <div className="game-container">
            <h2>{winner ? `Winner: ${winner}!` : `Current Player: ${currentPlayer}`}</h2>

            <div 
                className="board"
                style={{ 
                    display: "grid",
                    gridTemplateColumns: `repeat(${activeSize}, 1fr)`,
                    gridTemplateRows: `repeat(${activeSize}, 1fr)`,
                }}
            >
                {board.slice(0, activeSize * activeSize).map((cell, index) => (
                    <div
                        key={index} 
                        className={`cell ${cell ? "active" : ""}`}
                        onClick={() => !winner && handleClick(index)} // Prevent clicking after win
                    >
                        {cell}
                    </div>
                ))}
            </div>

            <button onClick={resetGame} className="reset-btn">Restart</button>
        </div>
    );
};

export default Board;
