import { useState } from "react";

const FULL_SIZE = 50; // Fixed full board size
const INITIAL_ACTIVE_SIZE = 7; // Initial playable area
const EXPANSION_MARGIN = 1; // How close to the edge to expand
const EXPANSION_AMOUNT = 4; // Increase active area by this amount

const useGameLogic = () => {
    const [board, setBoard] = useState(Array(FULL_SIZE * FULL_SIZE).fill(null));
    const [currentPlayer, setCurrentPlayer] = useState("X");
    const [winner, setWinner] = useState(null);
    const [activeSize, setActiveSize] = useState(INITIAL_ACTIVE_SIZE);

    const handleClick = (index) => {
        if (board[index] !== null || winner) return;

        const row = Math.floor(index / FULL_SIZE);
        const col = index % FULL_SIZE;

        // Expand board if near the edge of active area
        let shouldExpand = 
            row < EXPANSION_MARGIN ||
            col < EXPANSION_MARGIN ||
            row >= activeSize - EXPANSION_MARGIN ||
            col >= activeSize - EXPANSION_MARGIN;

        // Update board state
        const newBoard = [...board];
        newBoard[index] = currentPlayer;
        setBoard(newBoard);

        if (checkWinner(newBoard, activeSize, currentPlayer)) {
            setWinner(currentPlayer);
        } else {
            setCurrentPlayer(currentPlayer === "X" ? "O" : "X");
        }

        // Expand board AFTER updating move
        if (shouldExpand) expandActiveBoard(row, col);
    };

    // Expands the playable board dynamically
    const expandActiveBoard = (row, col) => {
        let newSize = activeSize;

        if (row < EXPANSION_MARGIN || row >= activeSize - EXPANSION_MARGIN) {
            newSize = Math.min(activeSize + EXPANSION_AMOUNT, FULL_SIZE);
        }
        if (col < EXPANSION_MARGIN || col >= activeSize - EXPANSION_MARGIN) {
            newSize = Math.min(activeSize + EXPANSION_AMOUNT, FULL_SIZE);
        }

        setActiveSize(newSize);
    };

    // Checks for 5-in-a-row win condition
    const checkWinner = (board, size, currentPlayer) => {
        const grid = [];

        // Convert 1D board into a 2D grid based on FULL_SIZE
        for (let row = 0; row < size; row++) {
            grid.push(board.slice(row * FULL_SIZE, (row + 1) * FULL_SIZE));
        }

        const checkLine = (cells) => {
            for (let i = 0; i <= cells.length - 5; i++) {
                if (cells.slice(i, i + 5).every(cell => cell === currentPlayer)) {
                    return true;
                }
            }
            return false;
        };

        // Check rows
        for (let row = 0; row < size; row++) {
            if (checkLine(grid[row].slice(0, size))) return true;
        }

        // Check columns
        for (let col = 0; col < size; col++) {
            let column = grid.map(row => row[col]);
            if (checkLine(column)) return true;
        }

        // Check diagonals
        for (let startRow = 0; startRow <= size - 5; startRow++) {
            for (let startCol = 0; startCol <= size - 5; startCol++) {
                let mainDiagonal = [];
                let antiDiagonal = [];

                for (let i = 0; i < 5; i++) {
                    mainDiagonal.push(grid[startRow + i]?.[startCol + i]);
                    antiDiagonal.push(grid[startRow + i]?.[startCol + 4 - i]);
                }

                if (checkLine(mainDiagonal) || checkLine(antiDiagonal)) return true;
            }
        }
        return false;
    };

    const resetGame = () => {
        setBoard(Array(FULL_SIZE * FULL_SIZE).fill(null));
        setCurrentPlayer("X");
        setWinner(null);
        setActiveSize(INITIAL_ACTIVE_SIZE); // Reset board to starting size
    };

    return { board, currentPlayer, winner, activeSize, FULL_SIZE, handleClick, resetGame };
};

export default useGameLogic;
